document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("editModal");t.addEventListener("show.bs.modal",function(t){const e=t.relatedTarget,o=e.getAttribute("data-id"),n=e.getAttribute("data-nombres"),a=e.getAttribute("data-apellidos"),d=e.getAttribute("data-correo"),i=e.getAttribute("data-telefono"),r=e.getAttribute("data-puesto"),l=e.getAttribute("data-turno"),u=e.getAttribute("data-habilitado");document.getElementById("edit-id").value=o,document.getElementById("edit-nombres").value=n,document.getElementById("edit-apellidos").value=a,document.getElementById("edit-correo").value=d,document.getElementById("edit-telefono").value=i,document.getElementById("edit-puesto").value=r,document.getElementById("edit-turno").value=l;const c=document.getElementById("habilitado");c.checked="1"===u})}),document.getElementById("formEditEmpleado").addEventListener("submit",function(t){t.preventDefault(),Swal.fire({title:"¿Estás seguro?",text:"¿Quieres editar este empleado?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Sí, crear!",cancelButtonText:"Cancelar"}).then(t=>{t.isConfirmed&&this.submit()})});const urlParams=new URLSearchParams(window.location.search);urlParams.has("success")&&"true"===urlParams.get("success")&&Swal.fire({position:"top-end",icon:"success",title:"Empleado creado exitosamente",showConfirmButton:!1,timer:1500}).then(()=>{const t=window.location.pathname+window.location.search.split("&")[0];window.location.replace(t)});