document.getElementById("limpiar").addEventListener("click",function(e){e.preventDefault(),Swal.fire({title:"¿Limpiar los datos?",icon:"info",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Si",cancelButtonText:"No"}).then(e=>{e.isConfirmed&&document.getElementById("formCrearUsuario").reset()})}),document.getElementById("formCrearUsuario").addEventListener("submit",function(e){e.preventDefault(),Swal.fire({title:"¿Estás seguro?",text:"¿Quieres crear este nuevo empleado?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Sí, crear!",cancelButtonText:"Cancelar"}).then(e=>{e.isConfirmed&&this.submit()})});