document.getElementById("limpiar").addEventListener("click",function(e){e.preventDefault(),Swal.fire({title:"¿Limpiar los datos?",icon:"info",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Si",cancelButtonText:"No"}).then(e=>{e.isConfirmed&&document.getElementById("formCrearUsuario").reset()})}),document.getElementById("formCrearUsuario").addEventListener("submit",function(e){e.preventDefault(),Swal.fire({title:"¿Estás seguro?",text:"¿Quieres crear este nuevo empleado?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Sí, crear!",cancelButtonText:"Cancelar"}).then(e=>{e.isConfirmed&&this.submit()})});const urlParams=new URLSearchParams(window.location.search);urlParams.has("success")&&"true"===urlParams.get("success")&&Swal.fire({position:"top-end",icon:"success",title:"Empleado editado exitosamente",showConfirmButton:!1,timer:1500}).then(()=>{const e=window.location.pathname+window.location.search.split("&")[0];window.location.replace(e)});